targets.expr:
sum(
  rate(
    nginx_ingress_controller_requests{status!~"[4-5].*", controller_class=~"$controller_class",exported_namespace=~"$namespace",ingress=~"$ingress"}[${__range_s}s]
      )
   )   
/ 
(
  sum(
    rate(
      nginx_ingress_controller_requests{ controller_class=~"$controller_class",exported_namespace=~"$namespace",ingress=~"$ingress"}[${__range_s}s]
        )
     ) - 
  (
  sum(
    rate(
      nginx_ingress_controller_requests{status=~"404|499", controller_class=~"$controller_class",exported_namespace=~"$namespace",ingress=~"$ingress"}[${__range_s}s]
        )
     ) 
  or vector(0)
  )
)

sum by (le)(
  increase(
    nginx_ingress_controller_request_duration_seconds_bucket{
      status!="404",status!="500",
      controller_class =~ "$controller_class",
      exported_namespace =~ "$namespace",
      ingress =~ "$ingress"
    }[$__interval]
  )
)

sum(
  rate(
    nginx_ingress_controller_requests{status!~"[4-5].*", controller_class=~"$controller_class", ingress=~"$ingress", exported_namespace=~"$namespace"}[${__range_s}s]
      )
   ) by (ingress)
/ 
(
  sum(
    rate(
      nginx_ingress_controller_requests{ controller_class=~"$controller_class", ingress=~"$ingress", exported_namespace=~"$namespace"}[${__range_s}s]
        )
     ) by (ingress)
     - 
  (
  sum(
    rate(
      nginx_ingress_controller_requests{status=~"404|499", controller_class=~"$controller_class", ingress=~"$ingress",exported_namespace=~"$namespace"}[${__range_s}s]
        )
     ) by (ingress)
  or vector(0)
  )
)
